import React from "react";
import { useState } from 'react';
import "./Auth.css";
import Logo from "../img/LifeVenture-logo-no-text.png";

const Auth = () => {
  const [isSignUp, setIsSignUp] = useState(false);

  const [data, setData] = useState({ firstname: "", lastname: "", password: "", confirmpass: "", username: "" })

  const handleChange = (e) => {
    setData({...data, [e.target.name]: e.target.value})
  }

  return (
    <div className="Auth">
      {/* Left Side */}
      <div className="a-left">
        <img src={Logo} />
        <div className="Webname">
          <h1>LifeVenture</h1>
          <h6>
            <span>Go expolore.</span>
            <span>Share stories.</span>
            <span>Live the adventure.</span>
          </h6>
        </div>
      </div>
      {/* Right Side */}
      <div className="right-side">
        <form className="infoForm">
          <h3>{isSignUp ? "Sign up" : "Log In"}</h3>
          {isSignUp && (
            <div>   
              <input
                type="text"
                placeholder="First Name"
                className="infoInput"
                name="firstname"
                onChange={handleChange}
              />
              <input
                type="text"
                placeholder="Last Name"
                className="infoInput"
                name="lastname"
                onChange={handleChange}
              />
          </div>
          )}
          <div>
            <input
              type="text"
              className="infoInput"
              placeholder="Username"
              name="username"
              onChange={handleChange}
            />
          </div>
          <div>
            <input
              type="text"
              className="infoInput"
              placeholder="Password"
              name="password"
              onChange={handleChange}
            />
            {isSignUp && (
              <input
                type="text"
                className="infoInput"
                placeholder="Confirm password"
                name="confirmpass"
                onChange={handleChange}
              />
            )}

          </div>
          <div>
            <span>
              {isSignUp ? "Alreday have an account?" : "Don't have an account?"}
            </span>
            <span onClick={()=> {setIsSignUp((prev)=>(!prev))}} style={{cursor: "pointer"}}>
              {isSignUp ? "Login" : "Sign up"}
            </span>
          </div>
          <button className="button infoButton">{isSignUp ? "Sign up" : "Log In"}</button>
        </form>
      </div>
    </div>
  );
};

export default Auth;
